mapper.select:
select s.id as id, s.name as name, s.room_id as room_id, s.faculty_id as faculty_id, s.number as number, s.registration_date as registration_date, s.photo as photo, s.phone as phone, s.is_leave as is_leave, r.id as r_id, r.number as r_number, f.id as f_id, f.name as f_name from student s left join room r on s.room_id = r.id left join faculty f on s.faculty_id = f.id
instr(s.name, #{s.name})
and s.room_id = #{s.roomId}
and instr(s.number, #{s.number})
and s.is_leave = #{s.isLeave}
and s.faculty_id in
#{item}
and r.building_id in
#{item}
and (is_delete = 0 or is_delete is null)
order by s.id desc

mapper.update:
update student
set photo = #{photo}
where id = #{id}

mapper.select:
select count(*)
from student
where room_id = #{rId};

mapper.update:
update student
set is_leave = #{isLeave}
where id = #{id}
